fun plusMinus(arr: Array<Int>): Unit {
    var positive = 0
    var negative = 0
    var zeroes = 0

    for (i in arr) {
        when {
            i > 0 -> positive++
            i < 0 -> negative++
            else -> zeroes++
        }
    }

    println(1.0 / arr.size * positive)
    println(1.0 / arr.size * negative)
    println(1.0 / arr.size * zeroes)
}

