}

type person struct {
  firstName string
  lastName string
  contactInfo
}

func main() {
  jim := person{
    firstName: "Jim",
    lastName: "Party",
    contactInfo: contactInfo{
      email: "jim@gmail.com",
      zipCode: 94000
    }
  }

  fmt.Printf("%+v", jim)
  // same as
  jim.print()

  // Update name
  jim.updateNameIncorrect("jimmy")
  jim.print() // still shows jim instead of jimmy

  // Correct
  jimPointer := &jim
  jimPointer.updateName("jimmy")
  jim.print() // prints jimmy

  // Also works - shortcut for the receiver
  jim.updatename("jimmy")
  jim.print()
}

