AWS::CloudFormation::Init:
  config:
    # where to unpack and from where
    files:
      /tmp/setup.mysql:
        content: !Sub |
          CREATE DATABASE ${DBName};
          CREATE USER '${DBUsername}'@'localhost' IDENTIFIED BY '${DBPassword}';
          GRANT ALL ON ${DBName}.* TO '${DBUsername}'@'localhost';
          FLUSH PRIVILEDGES;
        mode: '000644'
        owner: 'root'
        group: 'root'
