// src/main/java/Tree.java
import main.java.Node;
import java.util.ArrayList;
import java.util.List;

class Tree {
    public Node root;

    public Tree() {
        this.root = null;
    }

    public Tree(Node root) {
        this.root = root;
    }

    public List<Integer> bfs() {
        if (this.root == null) {
            throw new NullPointerException("this.root is null");
        }

        List<Node> n = new ArrayList<>();
        n.add(this.root);

        List<Integer> res = new ArrayList<>();

        while (n.size() > 0) {
            Node child = n.remove(0);
            if (child.children != null) {
                n.addAll(child.children);
            }
            res.add(child.data);
        }

        return res;
    }

    public List<Integer> dfs() {
        if (this.root == null) {
            throw new NullPointerException("No root");
        }

        List<Node> n = new ArrayList<Node>();
        n.add(this.root);

        List<Integer> res = new ArrayList<Integer>();

        while (n.size() > 0) {
            Node child = n.remove(0);

            if (child.children != null) {
                n.addAll(0, child.children);
            }
            res.add(child.data);
        }

        return res;
    }
}

// src/main/java/Node.java
package main.java;

import java.util.ArrayList;
import java.util.List;

public class Node {
    public Integer data;
    public List<Node> children;

    public Node() {
        this.data = null;
        this.children = new ArrayList<>();
    }

    public Node(Integer data) {
        this.data = data;
        this.children = new ArrayList<>();
    }

    public Node(Integer data, List<Node> children) {
        this.data = data;
        this.children = new ArrayList<>();
        this.children.addAll(children);
    }
}

