    var x = nil //error

    _ = x
}

package main

func main() {
    var x interface{} = nil

    _ = x
}

package main

func main() {
    var m map[string]int
    m["one"] = 1 //error
}

package main

func main() {
    var s []int
    s = append(s,1)
}

package main

import (
   "fmt"
)

func main() {
   a := make([]int, 99)
   fmt.Println(cap(a)) // 99

   b := make(map[string]int, 99)
   fmt.Println(len(b)) // 0
   fmt.Println(cap(b)) // error: ./prog.go:12:17: invalid argument b (type map[string]int) for cap
}

package main

func main() {
    var x string = nil //error

    if x == nil { //error
        x = "default"
    }
}

package main

func main() {
    var x string //defaults to "" (zero value)

    if x == "" {
        x = "default"
    }
}

package main

import "fmt"

func main() {
    x := [3]int{1,2,3}

    func(arr [3]int) {
        arr[0] = 7
        fmt.Println(arr) //prints [7 2 3]
    }(x)

    fmt.Println(x) //prints [1 2 3] (not ok if you need [7 2 3])
}

package main

import "fmt"

func main() {
    x := [3]int{1,2,3}

    func(arr *[3]int) {
        (*arr)[0] = 7
        fmt.Println(arr) //prints &[7 2 3]
    }(&x)

    fmt.Println(x) //prints [7 2 3]
}

package main

import "fmt"

func main() {
    x := []int{1,2,3}

    func(arr []int) {
        arr[0] = 7
        fmt.Println(arr) //prints [7 2 3]
    }(x)

    fmt.Println(x) //prints [7 2 3]
}

