function Division(num1, num2) {
  const max = Math.max(num1, num2);
  const min = Math.min(num1, num2);
  if (max % min === 0) {
    return min;
  }

  let res = 1;
  for (let i = 0; i <= min / 2; i++) {
    if (max % i === 0 && min % i === 0) {
      res = i;
    }
  }

  return res;
}

