<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#adding-x-and-y-axis"}}>{`Adding X and Y axis`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#flipping-the-axes"}}>{`Flipping the axes`}</MDXTag></MDXTag>
