<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var data = [123, 52, 46, 30, 4];

const results = data.filter((entry) => {
    return entry > 50;
});

const mapping = data.map((entry) => {
    console.log(entry.key);
    console.log(entry.value);
