<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`const example = d3.min(data);
console.log(example);       // values returned

const dataLoHiValue = d3.extent(data);  // return min/max as array

var dictMinValue = d3.min(dounts, (d, i) {
    return d.value;
});
`}</MDXTag></MDXTag>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"programmatic-svgs"}}>{`Programmatic SVGs`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var data = [132,71,337,93,78,43,20,16,30,8,17,21];
let svg = d3.select('body').append('svg')
                .attr('id', 'chart')
                .attr('height', 450)
                .attr('width', 800);

// creating the bars
// vertical bar graph
svg.selectAll('.bar')
    .data(data)
    .enter()                // enter phase
    .append('rect')
    .attr('class', 'bar')   // for future selections
    .attr('x', 0)
    .attr('y', (d i) => {
        return i * 20;
    })
    .attr('width', (d, i) => {
        return d;
    })
    .attr('height', 19);
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"scaling-data"}}>{`Scaling Data`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Creating scaling functions for both x and y.`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var data = [132, 71, 337, 93, 78, 43, 20, 16, 30, 8, 17, 21];
let w = 800;
let h = 450;
let x = d3.scale
    .linear()
    .domain([0, d3.max(data)])
    .range([0, w]);
let y = d3.scale
    .linear()
    .domain([0, data.length])
    .range([0, h]);

let svg = d3
    .select('body')
    .append('svg')
    .attr('id', 'chart')
    .attr('height', h)
    .attr('width', w);

// creating the bars
// vertical bar graph
svg
    .selectAll('.bar')
    .data(data)
    .enter() // enter phase
    .append('rect')
    .attr('class', 'bar') // for future selections
    .attr('x', 0)
    .attr('y', (d, i) => {
        return y(i);
    })
    .attr('width', (d, i) => {
        return x(d); // x() does the scaling
    })
    .attr('height', (d, i) => {
        return y(1) - 1;
    });
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"styling-with-css"}}>{`Styling with CSS`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Getting rid of the aliasing`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.bar {
    fill: purple;
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Not that `}<MDXTag name="inlineCode" components={components} parentName="p">{`attr('class', 'bar')`}</MDXTag>{` will manually reset the class value, so you can also use `}<MDXTag name="inlineCode" components={components} parentName="p">{`.classed('bar', true)`}</MDXTag>{` - true to add the class, false to remove.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"adding-text-to-the-chart"}}>{`Adding Text to the chart`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Creating scaling functions for both x and y.`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var data = [132, 71, 337, 93, 78, 43, 20, 16, 30, 8, 17, 21];
let w = 800;
let h = 450;
let x = d3.scale
    .linear()
    .domain([0, d3.max(data)])
    .range([0, w]);
let y = d3.scale
    .linear()
    .domain([0, data.length])
    .range([0, h]);

let svg = d3
    .select('body')
    .append('svg')
    .attr('id', 'chart')
    .attr('height', h)
    .attr('width', w);

function plot(params) {
    // creating the bars
    // vertical bar graph
    this.selectAll('.bar')
        .data(params.data)
        .enter() // enter phase
        .append('rect')
        .attr('class', 'bar') // for future selections
        .attr('x', 0)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('width', (d, i) => {
            return x(d); // x() does the scaling
        })
        .attr('height', (d, i) => {
            return y(1) - 1;
        });

    this.selectAll('.bar-label')
        .data(params.data)
        .enter()
        .append('text')
        .classed('bar-label', true)
        .attr('x', (d, i) => {
            return x(d); // use css to change the anchor
        })
        .attr('dx', -4)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('dy', (d, i) => {
            return y(1) / 1.5 + 2;
        })
        .text((d, i) => {
            return d;
        });
}

// first arg will be what is referenced by "this"
plot.call(svg, {
    data: data
});
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"using-svg-groups"}}>{`Using SVG groups`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`SVG groups are like a div that are a convenience element to allow children to be moved and affected together.`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var data = [132, 71, 337, 93, 78, 43, 20, 16, 30, 8, 17, 21];
let w = 800;
let h = 450;
let margin = {
    top: 20,
    bottom: 20,
    left: 20,
    right: 20
};

var width = w - margin.left - margin.right;
var height = h - margin.top - margin.bottom;

let x = d3.scale
    .linear()
    .domain([0, d3.max(data)])
    .range([0, width]);
let y = d3.scale
    .linear()
    .domain([0, data.length])
    .range([0, height]);

let svg = d3
    .select('body')
    .append('svg')
    .attr('id', 'chart')
    .attr('height', h)
    .attr('width', w);

let chart = svg
    .append('g')
    .classed('display', true)
    .attr('transform', 'translate(20, 20)');

function plot(params) {
    // creating the bars
    // vertical bar graph
    this.selectAll('.bar')
        .data(params.data)
        .enter() // enter phase
        .append('rect')
        .attr('class', 'bar') // for future selections
        .attr('x', 0)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('width', (d, i) => {
            return x(d); // x() does the scaling
        })
        .attr('height', (d, i) => {
            return y(1) - 1;
        });

    this.selectAll('.bar-label')
        .data(params.data)
        .enter()
        .append('text')
        .classed('bar-label', true)
        .attr('x', (d, i) => {
            return x(d); // use css to change the anchor
        })
        .attr('dx', -4)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('dy', (d, i) => {
            return y(1) / 1.5 + 2;
        })
        .text((d, i) => {
            return d;
        });
}

// first arg will be what is referenced by "this"
plot.call(chart, {
    data: data
});
`}</MDXTag></MDXTag>
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h1" components={components} props={{"id":"section-4-making-a-complex-chart"}}>{`Section 4: Making a Complex Chart`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"working-with-arrays-of-objects"}}>{`Working with Arrays of Objects`}</MDXTag>
<MDXTag name="p" components={components}>{`If working with a dict, we need an accessor function!`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`var data = [
    { key: 'Glazed', value: 132 },
    { key: 'Jelly', value: 71 },
    { key: 'Holes', value: 337 },
    { key: 'Sprinkles', value: 93 },
    { key: 'Crumb', value: 78 },
    { key: 'Chocolate', value: 43 },
    { key: 'Coconut', value: 20 },
    { key: 'Cream', value: 16 },
    { key: 'Cruller', value: 30 },
    { key: 'clair', value: 8 },
    { key: 'Fritter', value: 17 },
    { key: 'Bearclaw', value: 21 }
];

let w = 800;
let h = 450;
let margin = {
    top: 20,
    bottom: 20,
    left: 20,
    right: 20
};

var width = w - margin.left - margin.right;
var height = h - margin.top - margin.bottom;

let x = d3.scale
    .linear()
    .domain([
        0,
        d3.max(data, (d) => {
            return d.value;
        })
    ])
    .range([0, width]);

let y = d3.scale
    .linear()
    .domain([0, data.length])
    .range([0, height]);

let svg = d3
    .select('body')
    .append('svg')
    .attr('width', 800)
    .attr('height', 420)
    .attr('id', 'chart');
let chart = svg
    .append('g')
    .classed('display', true)
    .attr('transform', 'translate(20, 20)');

function plot(params) {
    // creating the bars
    // vertical bar graph
    this.selectAll('.bar')
        .data(params.data)
        .enter() // enter phase
        .append('rect')
        .attr('class', 'bar') // for future selections
        .attr('x', 0)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('width', (d, i) => {
            return x(d.value); // x() does the scaling
        })
        .attr('height', (d, i) => {
            return y(1) - 1;
        });

    this.selectAll('.bar-label')
        .data(params.data)
        .enter()
        .append('text')
        .classed('bar-label', true)
        .attr('x', (d, i) => {
            return x(d.value); // use css to change the anchor
        })
        .attr('dx', -4)
        .attr('y', (d, i) => {
            return y(i);
        })
        .attr('dy', (d, i) => {
            return y(1) / 1.5 + 2;
        })
        .text((d, i) => {
            return d.value;
        });
}

plot.call(chart, {
    data: data
});
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"creating-an-ordinal-scale"}}>{`Creating an Ordinal Scale`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`var data = [
    {key: "Glazed",     value: 132},
    {key: "Jelly",      value: 71},
    {key: "Holes",      value: 337},
    {key: "Sprinkles",  value: 93},
    {key: "Crumb",      value: 78},
    {key: "Chocolate",  value: 43},
    {key: "Coconut",    value: 20},
    {key: "Cream",      value: 16},
    {key: "Cruller",    value: 30},
    {key: "clair",     value: 8},
    {key: "Fritter",    value: 17},
    {key: "Bearclaw",   value: 21}
];

let w = 800;
let h = 450;
let margin = {
    top: 20,
    bottom: 20,
    left: 20,
    right: 20
};

var width = w - margin.left - margin.right;
var height = h - margin.top - margin.bottom;

let x = d3.scale.linear()
        .domain([0, d3.max(data, (d) => {
            return d.value;
    })])
        .range([0, width]);
var y = d3.scale.ordinal()          // need distinct values eg keys
        .domain(data.map((entry) => {
            return entry.key;
        }))
        .rangeBands([0, height]);   // used for distinct values

let svg = d3.select('body').append('svg')
                        .attr('width', 800)
            .attr('height', 420)
            .attr('id', 'chart');
let chart = svg.append('g')
                .classed('display', true)
        .attr('transform', 'translate(20, 20)');

function plot(params) {
    // creating the bars
    // vertical bar graph
    this.selectAll('.bar')
        .data(params.data)
        .enter()                // enter phase
        .append('rect')
        .attr('class', 'bar')   // for future selections
        .attr('x', 0)
        .attr('y', (d, i) => {
            return y(d.key);
        })
        .attr('width', (d, i) => {
            return x(d.value);      // x() does the scaling
        })
        .attr('height', (d, i) => {
            return y.rangeBand() - 1;
        });

    this.selectAll('.bar-label')
        .data(params.data)
        .enter()
        .append('text')
        .classed('bar-label', true)
        .attr('x', (d, i) => {
            return x(d.value);          // use css to change the anchor
        })
        .attr('dx', -4)
        .attr('y', (d, i) => {
            return y(d.key);
        })
        .attr('dy', (d, i) => {
