  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"swift-3-extensions"}}>{`Swift 3 Extensions`}</MDXTag>
{/* TOC */}
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#swift-3-extensions"}}>{`Swift 3 Extensions`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#extensions"}}>{`Extensions`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#-----computed-properties"}}>{`---- Computed Properties`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#-----mutating-instance-methods"}}>{`---- Mutating Instance Methods`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#-----subscripts"}}>{`---- Subscripts`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#-----nested-types"}}>{`---- Nested Types`}</MDXTag></MDXTag>
</MDXTag></MDXTag>
</MDXTag></MDXTag>
</MDXTag>
{/* /TOC */}
<MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"extensions"}}>{`Extensions`}</MDXTag>
<MDXTag name="p" components={components}>{`Extensions add new functionality to an existing class, structure, enumeration, or protocol type.`}</MDXTag>
<MDXTag name="p" components={components}>{`This includes the ability to extend types for which you do not have access to the original source code (known as retroactive modeling). Extensions are similar to categories in Objective-C. (Unlike Objective-C categories, Swift extensions do not have names.)`}</MDXTag>
<MDXTag name="p" components={components}>{`Extensions in Swift can:`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul">{`Add computed instance properties and computed type properties`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Define instance methods and type methods`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Provide new initializers`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Define subscripts`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Define and use new nested types`}</MDXTag>
<MDXTag name="li" components={components} parentName="ul">{`Make an existing type conform to a protocol`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`extension SomeType: SomeProtocol, AnotherProtocol {
    // implementation of protocol requirements goes here
}
`}</MDXTag></MDXTag>
<div id="computed"></div>
<MDXTag name="h3" components={components} props={{"id":"-----computed-properties"}}>{`---- Computed Properties`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`struct Size {
    var width = 0.0, height = 0.0
}
struct Point {
    var x = 0.0, y = 0.0
}
struct Rect {
    var origin = Point()
    var size = Size()
}

let defaultRect = Rect()
let memberwiseRect = Rect(origin: Point(x: 2.0, y: 2.0), size: Size(width: 5.0, height: 5.0))

extension Rect {
    init(center: Point, size: Size) {
        let originX = center.x - (size.width / 2)
        let originY = center.y - (size.height / 2)
        self.init(origin: Point(x: originX, y: originY), size: size)
    }
}

let centerRect = Rect(center: Point(x: 4.0, y: 4.0),
                      size: Size(width: 3.0, height: 3.0))
// centerRect's origin is (2.5, 2.5) and its size is (3.0, 3.0)
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Extensions can add new instance methods and type methods to existing types. The following example adds a new instance method called repetitions to the Int type:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`extension Int {
    func repetitions(task: () -> Void) {
        for _ in 0..<self {
            task()
        }
    }
}

3.repetitions {
    print("Hello!")
}
// Hello!
// Hello!
// Hello!
`}</MDXTag></MDXTag>
<div id="mutating"></div>
<MDXTag name="h3" components={components} props={{"id":"-----mutating-instance-methods"}}>{`---- Mutating Instance Methods`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`extension Int {
    mutating func square() {
        self = self * self
    }
}
var someInt = 3
someInt.square()
// someInt is now 9
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"-----subscripts"}}>{`---- Subscripts`}</MDXTag>
<MDXTag name="p" components={components}>{`Extensions can add new subscripts to an existing type. This example adds an integer subscript to Swiftâ€™s built-in Int type. This subscript `}{`[n]`}{` returns the decimal digit n places in from the right of the number:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`123456789[0] returns 9
123456789[1] returns 8
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`extension Int {
    subscript(digitIndex: Int) -> Int {
        var decimalBase = 1
        for _ in 0..<digitIndex {
            decimalBase *= 10
        }
        return (self / decimalBase) % 10
    }
}
746381295[0]
// returns 5
746381295[1]
// returns 9
746381295[2]
// returns 2
746381295[8]
// returns 7

746381295[9]
// returns 0, as if you had requested:
0746381295[9]
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"-----nested-types"}}>{`---- Nested Types`}</MDXTag>
<MDXTag name="p" components={components}>{`Extensions can add new nested types to existing classes, structures, and enumerations:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`extension Int {
    enum Kind {
        case negative, zero, positive
    }
    var kind: Kind {
        switch self {
        case 0:
            return .zero
        case let x where x > 0:
            return .positive
        default:
            return .negative
        }
    }
}

// The nested enumeration can now be used with any Int value:

func printIntegerKinds(_ numbers: [Int]) {
    for number in numbers {
        switch number.kind {
        case .negative:
            print("- ", terminator: "")
        case .zero:
            print("0 ", terminator: "")
        case .positive:
            print("+ ", terminator: "")
        }
    }
    print("")
}
printIntegerKinds([3, 19, -27, 0, -6, 0, 7])
// Prints "+ + - 0 - 0 + "
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
