  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"handling-images"}}>{`Handling Images`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"useful-links"}}>{`Useful links`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://varvy.com/pagespeed/defer-images.html"}}>{`https://varvy.com/pagespeed/defer-images.html`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://developers.google.com/web/fundamentals/web-app-manifest/"}}>{`https://developers.google.com/web/fundamentals/web-app-manifest/`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://caniuse.com/#feat=webp"}}>{`https://caniuse.com/#feat=webp`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://css-tricks.com/using-webp-images/"}}>{`https://css-tricks.com/using-webp-images/`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://github.com/verlok/lazyload"}}>{`https://github.com/verlok/lazyload`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://afarkas.github.io/lazysizes/index.html"}}>{`https://afarkas.github.io/lazysizes/index.html`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"deferring-web-loaded-images"}}>{`Deferring Web loaded images`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"basics"}}>{`Basics`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-html"}}>{`<img src="data:image/png;base64,R0lGODlhAQABAAD/ACwAAAAAAQABAAACADs=" data-src="your-image-here">
`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`<script>
function init() {
var imgDefer = document.getElementsByTagName('img');
for (var i=0; i<imgDefer.length; i++) {
if(imgDefer[i].getAttribute('data-src')) {
imgDefer[i].setAttribute('src',imgDefer[i].getAttribute('data-src'));
} } }
window.onload = init;
</script>
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"using-lazyloadjs"}}>{`Using Lazyload.js`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`HTML`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-html"}}>{`<img class="lazy" alt="..." 
     data-src="../img/44721746JJ_15_a.jpg"
     width="220" height="280">
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`Javascript`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`import LazyLoad from "vanilla-lazyload";
var myLazyLoad = new LazyLoad({
    elements_selector: ".lazy"
});
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
